import React, { useEffect, useState } from 'react'
import api from '../api'

export default function Movies(){
  const [movies, setMovies] = useState([])
  const [title, setTitle] = useState('')
  const [desc, setDesc] = useState('')
    const [du, setDuration] = useState('')

  async function fetchMovies(){
    try{
      const res = await api.get('/movie')
      setMovies(res.data || [])
    }catch(err){ console.error(err) }
  }

  useEffect(()=>{ fetchMovies() }, [])

  async function add(e){
    e.preventDefault()
    try{
      await api.post('/movie/post', {
        title, description: desc,
        durationmins: du,
        language: 'hindi',
        releasedate: '2025-09-15',
        country: 'india',
        genre: 'drama'
      })
        formData.append('movie', JSON.stringify(movieData));
    
    // Append file (you need an <input type="file" /> in your form)
    if (file) {
      formData.append('file', file);
    }

    await api.post('/movie/post', formData, {
      headers: { 'Content-Type': 'multipart/form-data' }
    });
      setTitle(''); setDesc(''); setDuration(''); fetchMovies()
    }catch(err){ console.error(err) }
  }

  return (
    <div>
      <h2 className="text-xl mb-3">Movies</h2>
      <div className="grid md:grid-cols-2 gap-4">
        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Add Movie</h3>
          <form onSubmit={add} className="space-y-2">
            <input required value={title} onChange={e=>setTitle(e.target.value)} placeholder="Title" className="w-full p-2 border rounded" />
            <textarea value={desc} onChange={e=>setDesc(e.target.value)} placeholder="Description" className="w-full p-2 border rounded" />
               <input required value={du} onChange={e=>setDuration(e.target.value)} placeholder="Duration" className="w-full p-2 border rounded" />
            <button className="px-3 py-1 bg-blue-600 text-white rounded">Add</button>
          </form>
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Movies List</h3>
          <div className="space-y-2">
            {movies.length===0 && <div className="text-sm text-gray-500">No movies yet</div>}
            {movies.map(m=> (
              <div key={m.id} className="p-2 border rounded">
                <div className="font-semibold">{m.title}</div>
                <div className="text-sm text-gray-600">{m.description}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}
