import React, { useEffect, useState } from 'react'
import api from '../api'

export default function Movies() {
  const [movies, setMovies] = useState([])
  const [title, setTitle] = useState('')
  const [desc, setDesc] = useState('')
  const [du, setDuration] = useState('')
  const [file, setFile] = useState(null)

  // Fetch movies from backend
  async function fetchMovies() {
    try {
      const res = await api.get('/movie/get')
      setMovies(res.data || [])
    } catch (err) {
      console.error(err)
    }
  }

  useEffect(() => { fetchMovies() }, [])

  // Add movie
  async function add(e) {
    e.preventDefault()

    if (!file) {
      alert('Please select a file!')
      return
    }

    const formData = new FormData()
    const movieData = {
      title,
      description: desc,
      durationmins: du,
      language: 'hindi',
      releasedate: '2025-09-15',
      country: 'india',
      genre: 'drama'
    }

    formData.append('movie', JSON.stringify(movieData))
    formData.append('file', file)

    try {
      await api.post('/movie/post', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      })

      // Reset form
      setTitle('')
      setDesc('')
      setDuration('')
      setFile(null)
      fetchMovies()
    } catch (err) {
      console.error(err)
    }
  }

  return (
    <div>
      <h2 className="text-xl mb-3">Movies</h2>
      <div className="grid md:grid-cols-2 gap-4">
        {/* Add Movie Form */}
        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Add Movie</h3>
          <form onSubmit={add} className="space-y-2">
            <input
              required
              value={title}
              onChange={e => setTitle(e.target.value)}
              placeholder="Title"
              className="w-full p-2 border rounded"
            />
            <textarea
              value={desc}
              onChange={e => setDesc(e.target.value)}
              placeholder="Description"
              className="w-full p-2 border rounded"
            />
            <input
              required
              value={du}
              onChange={e => setDuration(e.target.value)}
              placeholder="Duration"
              className="w-full p-2 border rounded"
            />
            <input
              type="file"
              required
              onChange={e => setFile(e.target.files[0])}
              className="w-full p-2 border rounded"
            />
            <button className="px-3 py-1 bg-blue-600 text-white rounded">Add</button>
          </form>
        </div>

        {/* Movies List */}
        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Movies List</h3>
          <div className="space-y-2">
            {movies.length === 0 && <div className="text-sm text-gray-500">No movies yet</div>}
            {movies.map(m => (
              <div key={m.id} className="p-2 border rounded">
                <div className="font-semibold">{m.title}</div>
                <div className="text-sm text-gray-600">{m.description}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}
