import React, { useState } from 'react'
import { useNavigate, Link } from 'react-router-dom'
import api from '../api'

export default function Login(){
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [loading, setLoading] = useState(false)
  const [err, setErr] = useState('')
  const navigate = useNavigate()

  async function submit(e){
    e.preventDefault()
    setErr('')
    setLoading(true)
    try{
    const res = await api.post('/auth/post/user/signin', { 
  email, 
  password 
})
      const token = res.data.token || res.data
      if (!token) throw new Error('Token not returned')
      localStorage.setItem('token', token)
      navigate('/movies')
    }catch(error){
      console.error(error)
      setErr(error.response?.data?.message || error.message || 'Login failed')
    } finally { setLoading(false) }
  }

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded shadow mt-8">
      <h2 className="text-2xl mb-4">Login</h2>
      {err && <div className="bg-red-100 text-red-700 p-2 mb-3 rounded">{err}</div>}
      <form onSubmit={submit} className="space-y-3">
        <input required value={email} onChange={e=>setEmail(e.target.value)} placeholder="Email" className="w-full p-2 border rounded" />
        <input required value={password} onChange={e=>setPassword(e.target.value)} placeholder="Password" type="password" className="w-full p-2 border rounded" />
        <button disabled={loading} className="w-full bg-blue-600 text-white p-2 rounded">{loading? 'Please wait...' : 'Login'}</button>
      </form>
      <div className="mt-3 text-sm">Don't have account? <Link to="/signup" className="text-blue-600">Signup</Link></div>
    </div>
  )
}
