import React, { useEffect, useState } from 'react'
import api from '../api'

export default function Movies() {
  const [movies, setMovies] = useState([])
  const [title, setTitle] = useState('')
  const [desc, setDesc] = useState('')
  const [du, setDuration] = useState('')
  const [lan, setLanguage] = useState('')
    const [rdate, setReleaseDate] = useState('')
    const [con, setCountry] = useState('') 
     const [gen, setGenre] = useState('')
  const [file, setFile] = useState(null)

  // Fetch movies from backend
  async function fetchMovies() {
    try {
      const res = await api.get('/movie/get')
      setMovies(res.data || [])
    } catch (err) {
      console.error(err)
    }
  }

  useEffect(() => { fetchMovies() }, [])

  // Add movie
  async function add(e) {
    e.preventDefault()

    if (!file) {
      alert('Please select a file!')
      return
    }

    const formData = new FormData()
    const movieData = {
      title,
      description: desc,
      durationmins: du,
      language: lan,
      releasedate: rdate,
      country: con,
      genre: gen
    }

    formData.append('movie', JSON.stringify(movieData))
    formData.append('file', file)

    try {
      await api.post('/movie/post', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      })

      // Reset form
      setTitle('')
      setDesc('')
      setDuration('')
      setLanguage('')
      setReleaseDate('')
      setCountry('')
      setGenre('')
      setFile(null)
      
      fetchMovies()
    } catch (err) {
      console.error(err)
    }
  }

  return (
    <div>
      <h2 className="text-xl mb-3">Movies</h2>
      <div className="grid md:grid-cols-2 gap-4">
        {/* Add Movie Form */}
        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Add Movie</h3>
          <form onSubmit={add} className="space-y-2">
            <input
              required
              value={title}
              onChange={e => setTitle(e.target.value)}
              placeholder="Title"
              className="w-full p-2 border rounded"
            />
            <textarea
              value={desc}
              onChange={e => setDesc(e.target.value)}
              placeholder="Description"
              className="w-full p-2 border rounded"
            />
            <input
              required
              value={du}
              onChange={e => setDuration(e.target.value)}
              placeholder="Duration"
              className="w-full p-2 border rounded"
            />
             <input
              required
              value={lan}
              onChange={e => setLanguage(e.target.value)}
              placeholder="Language"
              className="w-full p-2 border rounded"
            />
     <input
              type="date"
              required
              value={rdate}
              onChange={e => setReleaseDate(e.target.value)}
              className="w-full p-2 border rounded"
            />
             <input
              required
              value={con}
              onChange={e => setCountry(e.target.value)}
              placeholder="Country"
              className="w-full p-2 border rounded"
            />
            <input
              required
              value={gen}
              onChange={e => setGenre(e.target.value)}
              placeholder="Genre"
              className="w-full p-2 border rounded"
            />

            <input
              type="file"
              required
              onChange={e => setFile(e.target.files[0])}
              className="w-full p-2 border rounded"
            />
            <button className="px-3 py-1 bg-blue-600 text-white rounded">Add</button>
          </form>
        </div>



        <div className="bg-white p-4 rounded shadow">
  <h3 className="font-semibold mb-2">Movies List</h3>
  <div className="space-y-2">
    {movies.length === 0 && <div className="text-sm text-gray-500">No movies yet</div>}
    {movies.map(m => (
      <div key={m.id} className="p-2 border rounded flex gap-3 items-center">
        {/* ✅ Movie Poster */}
        <img
        src={`http://192.168.0.104:8080/api/movie/get/movieimage/${m.id}`}
          alt={m.title}
          className="w-25 h-25 object-cover rounded"
          onError={(e) => e.target.style.display = 'none'} 
        />

        {/* Movie Info */}
        <div>
          <div className="font-semibold">{m.title}</div>
          <div className="text-sm text-gray-600">{m.description}</div>
          <div className="text-xs text-gray-500">Released: {m.releasedate}</div>
        </div>
      </div>
    ))}
  </div>
</div>

      </div>
    </div>
  )
}
